import React, { useState, useEffect, useRef } from 'react';
import './Game.css';

const TRACKS = 4;
const TRACK_HEIGHT = 80;
const GAME_WIDTH = 800;
const GAME_HEIGHT = TRACKS * TRACK_HEIGHT;
const CAR_WIDTH = 60;
const CAR_HEIGHT = 40;
const PLAYER_SIZE = 30;
const PLAYER_X = GAME_WIDTH - 80;

const Game = () => {
  const [cars, setCars] = useState([]);
  const [playerTrack, setPlayerTrack] = useState(1);
  const [score, setScore] = useState(0);
  const [speed, setSpeed] = useState(1);
  const gameRef = useRef();
  const animationRef = useRef();

  const getTrackY = (track) => track * TRACK_HEIGHT + (TRACK_HEIGHT - CAR_HEIGHT) / 2;
  const getPlayerY = (track) => track * TRACK_HEIGHT + (TRACK_HEIGHT - PLAYER_SIZE) / 2;

  // Spawn cars randomly
  useEffect(() => {
    const spawnCar = () => {
      const track = Math.floor(Math.random() * TRACKS);
      const carSpeed = 2 + Math.random() * 2 + speed * 0.5;
      const colors = ['#ff4757', '#3742fa', '#2ed573', '#ffa502', '#ff6b81'];

      setCars(prev => [...prev, {
        id: Date.now() + Math.random(),
        x: -CAR_WIDTH,
        track,
        speed: carSpeed,
        color: colors[Math.floor(Math.random() * colors.length)]
      }]);
    };

    const interval = setInterval(spawnCar, Math.max(800 - speed * 50, 300));
    return () => clearInterval(interval);
  }, [speed]);

  // Game loop
  useEffect(() => {
    const gameLoop = () => {
      setCars(prev => {
        // Move cars and remove off-screen ones
        const updatedCars = prev
          .map(car => ({ ...car, x: car.x + car.speed }))
          .filter(car => car.x < GAME_WIDTH + CAR_WIDTH);

        // AI logic: Check for incoming cars and switch tracks
        const dangerousCars = updatedCars.filter(car => 
          car.track === playerTrack && 
          car.x > PLAYER_X - 150 && 
          car.x < PLAYER_X + PLAYER_SIZE + 50
        );

        if (dangerousCars.length > 0) {
          // Find safest track
          const trackSafety = Array(TRACKS).fill(0).map((_, track) => {
            const nearCars = updatedCars.filter(car => 
              car.track === track && 
              car.x > PLAYER_X - 200 && 
              car.x < PLAYER_X + 100
            );
            return { track, danger: nearCars.length };
          });

          const safestTrack = trackSafety.reduce((safest, current) => 
            current.danger < safest.danger ? current : safest
          ).track;

          if (safestTrack !== playerTrack) {
            setPlayerTrack(safestTrack);
          }
        }

        return updatedCars;
      });

      setScore(prev => prev + 1);
      setSpeed(prev => Math.min(prev + 0.001, 3));
    };

    animationRef.current = setInterval(gameLoop, 16);
    return () => clearInterval(animationRef.current);
  }, [playerTrack]);

  return (
    <div className="game-container">
      <div className="hud">
        <div>Time: {Math.floor(score / 60)}s</div>
        <div>Speed: {speed.toFixed(1)}x</div>
      </div>

      <div className="game-area" ref={gameRef}>
        {/* Track lines */}
        {Array(TRACKS + 1).fill().map((_, i) => (
          <div 
            key={i} 
            className="track-line" 
            style={{ top: i * TRACK_HEIGHT }}
          />
        ))}

        {/* Cars */}
        {cars.map(car => (
          <div
            key={car.id}
            className="car"
            style={{
              left: car.x,
              top: getTrackY(car.track),
              backgroundColor: car.color
            }}
          />
        ))}

        {/* Player */}
        <div
          className="player"
          style={{
            left: PLAYER_X,
            top: getPlayerY(playerTrack)
          }}
        />
      </div>
    </div>
  );
};

export default Game;
